import './CartItems.css';

const CartItems = () => {
    const {all_product, cartItems, removeFromCart} = useContext(ShopContext);

  return (
    <div className="cart-items">
        <div className="cartitems-format-main">
            <p>Products</p>
            <p>Title</p>
            <p>Price</p>
            <p>Quantity</p>
            <p>Total</p>
            <p>Remove</p>
        </div>
        <hr />
        <div>
            <div className="cartitems-format">
                <img src="" alt="" />
            </div>
        </div>
    </div>
  );
}

export default CartItems;
